vertexShader.source:


attribute vec3 position;				
attribute vec2 uv;
uniform mat4 matWVP;

varying vec2 vUV;

void main() 
{
	gl_Position = matWVP * vec4(position, 1.0);
	vUV = uv;
}

fragmentShader.source:


precision mediump float;

uniform sampler2D diffuse;
uniform vec3 addColor;
varying vec2 vUV;


void main() 
{				
	vec4 color = texture2D(diffuse, vUV) + vec4(addColor, 1.0);
	gl_FragColor = color;
}

vertexShader.source:


#version 450
#extension GL_ARB_separate_shader_objects : enable

layout (std140, push_constant) uniform PushConsts 
{
	mat4 MVP;
} pushConsts;

layout(location = 0) in vec3 aPosition;
layout(location = 1) in vec2 aUV;

layout(location = 0) out vec2 fragTexCoord;

void main() 
{
	gl_Position = pushConsts.MVP * vec4(aPosition, 1.0);
	fragTexCoord = aUV;
}

fragmentShader.source:


#version 450
#extension GL_ARB_separate_shader_objects : enable

layout(binding = 0) uniform sampler2D diffuse;

layout(location = 0) in vec2 fragTexCoord;

layout(location = 0) out vec4 outColor;

void main() 
{
	//outColor = vec4(fragTexCoord.x, fragTexCoord.y, 0.0, 1.0);
	outColor = texture(diffuse, fragTexCoord);
}
