shader.vertexShader:


attribute vec3 aPosition;
uniform mat4 matMVP;

void main() 
{
	gl_Position = matMVP * vec4(aPosition, 1.0);
}

shader.fragmentShader:


precision mediump float;
uniform float color;
void main() 
{				
	gl_FragColor = vec4(color, 0.0, 0.0, 1.0);
}

shader.vertexShader:


attribute vec3 aPosition;
uniform mat4 matMVP;

void main() 
{
	gl_Position = matMVP * vec4(aPosition, 1.0);
}

shader.fragmentShader:


precision mediump float;

uniform vec4 color;

void main() 
{				
	gl_FragColor = vec4(color);
}
