shader.vertexShader:
attribute vec3 aPosition;
attribute vec3 aColor;
attribute vec2 aUV;

varying vec3 vColor;
varying vec2 vUV;

void main() 
{
	gl_Position = vec4(aPosition, 1.0);
	vColor = aColor;
}

shader.fragmentShader:
precision mediump float;

varying vec3 vColor;
varying vec2 vUV;

void main() 
{
	gl_FragColor = vec4(1.0, 0.0, 0.0, 1.0);
}
