shader.vertexShader:
attribute vec3 aPosition;
attribute vec3 aColor;
attribute vec2 aUV;

varying vec3 vColor;
varying vec2 vUV;

void main() 
{
	gl_Position = vec4(aPosition, 1.0);
	vColor = aColor;
	vUV = aUV;
}

shader.fragmentShader:
precision mediump float;

uniform float uniA;
uniform sampler2D testSample;

varying vec3 vColor;
varying vec2 vUV;

void main() 
{
	vec4 col = texture2D(testSample, vUV);
	gl_FragColor = vec4(vUV.x, vUV.y, 0.0, 1.0);
}
