vertexShader.source:


attribute vec3 aPosition;				
attribute vec2 aUV;

uniform mat4 orthoMat;

varying vec2 vUV;

void main() 
{
	gl_Position = orthoMat * vec4(aPosition, 1.0);
	vUV = aUV;
}

fragmentShader.source:


precision mediump float;
uniform sampler2D fontAtlas;
varying vec2 vUV;

void main() 
{				
	vec4 color = texture2D(fontAtlas, vUV);
	gl_FragColor = vec4(0.0, 0.0, 0.0, color.r);
}

vertexShader.source:


#version 450
#extension GL_ARB_separate_shader_objects : enable

layout (std140, push_constant) uniform PushConsts 
{
	mat4 orthoMat;
} pushConsts;

layout(location = 0) in vec3 aPosition;
layout(location = 1) in vec2 aUV;

layout(location = 0) out vec2 vUV;

void main() 
{
	gl_Position = pushConsts.orthoMat * vec4(aPosition, 1.0);
	vUV = aUV;
}

fragmentShader.source:


#version 450
#extension GL_ARB_separate_shader_objects : enable

layout(binding = 0) uniform sampler2D fontAtlas;

layout(location = 0) in vec2 vUV;

layout(location = 0) out vec4 outColor;

void main() 
{
	vec4 color = vec4(1.0, 1.0, 1.0, texture(fontAtlas, vUV));
	outColor = vec4(0.0, 0.0, 0.0, color.r);
}
