vertexShader.source:


attribute vec3 aPosition;				
attribute vec2 aUV;
attribute vec3 aColor;

varying vec2 vUV;
varying vec3 vColor;

void main() 
{
	gl_Position = vec4(aPosition, 1.0);
	vUV = aUV;
	vColor = aColor;
}

fragmentShader.source:


precision mediump float;

varying vec3 vColor;
varying vec2 vUV;

void main() 
{				
	gl_FragColor = vec4(vColor, 1.0);
}
