vertexShader.source:


attribute vec3 aPosition;				
attribute vec2 aUV;

varying vec2 vUV;

void main() 
{
	gl_Position = vec4(aPosition, 1.0);
	vUV = aUV;
}

fragmentShader.source:


precision mediump float;

uniform vec3 color;
varying vec2 vUV;

void main() 
{				
	gl_FragColor = vec4(0.0, 0.0, 1.0, 1.0);
}
